import React from 'react';
import './css/App.css';

class App extends React.Component{

  constructor(props){
    super(props)
    this.state = {
      tweets: [],
      isLoaded:  false,
    }
  }

  componentDidMount(){
    fetch('http://localhost:5000/sentiment/rona')
      .then(res => res.json())
      .then(data => {
          this.setState({
            tweets: data,
            isLoaded: true
          })
          console.log(this.state.tweets)

      })
  }

  render (){

    return(
    <div className="App">
   <InputGroup className="mb-3">
    <InputGroup.Prepend>
      <InputGroup.Text id="basic-addon1">@</InputGroup.Text>
    </InputGroup.Prepend>
    <FormControl
      placeholder="Username"
      aria-label="Username"
      aria-describedby="basic-addon1"
    />
  </InputGroup>
      <ul>
        {this.state.tweets.map(tweet=>
          <li key = {tweet.id}>
            {tweet.text}
            <br/>
          </li>
        )}
      </ul>
    </div>
  )
  }
}

export default App
