import React from 'react';
import './App.css';

class App extends React.Component{

  constructor(props){
    super(props)
    this.state = {
      tweets: [],
      isLoaded:  false,
    }
  }

  componentDidMount(){
    fetch('http://localhost:5000/sentiment/shopify')
      .then(res => res.json())
      .then(data => {
          this.setState({
            tweets: data,
            isLoaded: true
          })
          console.log(this.state.tweets)

      })
  }

  render (){
    const text = this.state.isLoaded ? this.state.tweets[0].id : "loading..."

    return(
    <div className="App">
      <ul>
        {this.state.tweets.map(tweet=>
          <li key = {tweet.id}>
            {tweet.text}
            <br/>
          </li>
        )}
      </ul>
    </div>
  )
  }
}

export default App
